import React from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Checkbox } from '@/components/ui/checkbox';
import { Button } from '@/components/ui/button';

export default function SongForm({ formData, setFormData, handleSubmit, errors = {} }) {
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader>
        <CardTitle>Add New Songs</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <Input
            id="title"
            name="title"
            label="Song Title"
            value={formData.title}
            onChange={handleChange}
            error={errors.title}
          />
          <Input
            id="artist"
            name="artist"
            label="Artist Name"
            value={formData.artist}
            onChange={handleChange}
            error={errors.artist}
          />
          <Input
            as="select"
            id="genre"
            name="genre"
            label="Genre"
            value={formData.genre}
            onChange={handleChange}
            error={errors.genre}
          >
            <option value="">--Select the genre--</option>
            <option value="pop">Pop</option>
            {/* Add more genre options */}
          </Input>
          <Textarea
            id="lyrics"
            name="lyrics"
            label="Lyrics"
            value={formData.lyrics}
            onChange={handleChange}
            error={errors.lyrics}
          />
          <RadioGroup name="playlist" value={formData.playlist} onChange={handleChange}>
            <RadioGroupItem value="morning vibe" label="Morning Vibe" />
            <RadioGroupItem value="night time" label="Night Time" />
            <RadioGroupItem value="on repeat" label="On Repeat" />
          </RadioGroup>
          <Checkbox
            id="isFavorite"
            name="isFavorite"
            label="Add to favorites"
            checked={formData.isFavorite}
            onChange={handleChange}
          />
          <Button type="submit">Publish</Button>
        </form>
      </CardContent>
    </Card>
  );
}
