import React, { useState } from 'react';
import { toast } from 'react-toastify';

export default function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      toast.error('Please enter a search term');
      return;
    }

    try {
      // Fetch all songs
      const response = await fetch('http://localhost:3001/songs');
      const allSongs = await response.json();

      // Filter songs based on search term (case-insensitive)
      const filteredSongs = allSongs.filter(song => 
        song.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        song.genre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (song.lyrics && song.lyrics.toLowerCase().includes(searchTerm.toLowerCase()))
      );

      setSearchResults(filteredSongs);

      if (filteredSongs.length === 0) {
        toast.info('No songs found');
      }

    } catch (error) {
      console.error('Error:', error);
      toast.error('Search failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <form onSubmit={handleSearch} className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for songs by title, genre, or lyrics..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="btn btn-primary" type="submit">
            Search
          </button>
        </div>
      </form>

      {/* Display search results */}
      <div className="row">
        {searchResults.length > 0 ? (
          <div className="col">
            <h3>Search Results</h3>
            <div className="list-group">
              {searchResults.map(song => (
                <div key={song.id} className="list-group-item">
                  <h5>{song.title}</h5>
                  <p className="mb-1"><strong>Genre:</strong> {song.genre}</p>
                  <p className="mb-1"><strong>Playlist:</strong> {song.playlist}</p>
                  {song.lyrics && (
                    <p className="mb-0"><strong>Lyrics:</strong><br />
                      {song.lyrics.substring(0, 100)}...
                    </p>
                  )}
                </div>
              ))}
            </div>
          </div>
        ) : searchTerm && (
          <div className="col">
            <p>No results found</p>
          </div>
        )}
      </div>
    </div>
  );
}
