// Songbook.jsx
import React, { useState } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { toast } from 'react-toastify';
import SongCard from '../Components/SongCard'; 

export default function Songbook() {
  const [songs, setSongs] = useState([]);

  // GET
  const fetchSongs = async () => {
    try {
      const response = await fetch('http://localhost:3001/songs');
      if (!response.ok) throw new Error('Failed to fetch songs');
      const data = await response.json();
      setSongs(data);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to load songs');
    }
  };

  // DELETE
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/songs/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete song');
      setSongs(songs.filter(song => song.id !== id));
      toast.success('Song deleted successfully!');
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to delete song');
    }
  };

  fetchSongs();

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-4">
          <h2>Your Songs</h2>
          <button 
            className="btn btn-primary mb-3"
            onClick={fetchSongs}
          >
            Refresh Songs
          </button>
          <div className="list-group">
            {songs.map(song => (
              <div key={song.id} className="list-group-item d-flex justify-content-between align-items-center">
                <Link to={`/songbook/${song.id}`} className="text-decoration-none">
                  <h5 className="mb-1">{song.title}</h5>
                  <small className="text-muted">{song.genre}</small>
                </Link>
                <button 
                  className="btn btn-danger btn-sm ms-2"
                  onClick={() => handleDelete(song.id)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
        <div className="col-md-8 border-start">
          <Outlet />
        </div>
      </div>
    </div>
  );
}
