import React, { useState } from 'react';
import { toast } from 'react-toastify';
import SongForm from '../Components/SongForm';

export default function SongDetails() {
   const [song, setSong] = useState(null);
   const [isEditing, setIsEditing] = useState(false);
   const [editForm, setEditForm] = useState({
       title: '',
       artist: '',
       genre: '',
       lyrics: '',
       playlist: 'morning vibe',
       isFavorite: false
   });
   const [errors, setErrors] = useState({});
   const songId = window.location.pathname.split('/').pop();

   // Load song details when songId changes
   if (song?.id !== songId) {
       fetch(`http://localhost:3001/songs/${songId}`)
           .then(response => {
               if (!response.ok) throw new Error('Song not found');
               return response.json();
           })
           .then(data => {
               setSong(data);
               setEditForm(data);
           })
           .catch(error => {
               toast.error('Failed to load song details');
           });
   }

   //PATCH
   const handleEditSubmit = async (e) => {
       e.preventDefault();
       try {
           const response = await fetch(`http://localhost:3001/songs/${songId}`, {
               method: 'PATCH',
               headers: {
                   'Content-Type': 'application/json',
               },
               body: JSON.stringify(editForm)
           });

           if (!response.ok) throw new Error('Failed to update song');
           const updatedSong = await response.json();
           setSong(updatedSong);
           setIsEditing(false);
           toast.success('Changes saved successfully! üéµ');
       } catch (error) {
           toast.error('Failed to save changes. Please try again.');
       }
   };

   if (!song && !isEditing) {
       return <div className="mt-3">Loading song details...</div>;
   }

   return (
       <div>
           {song && !isEditing ? (
               <div className="card">
                   <div className="card-body">
                       <h2>{song.title}</h2>
                       <p><strong>Genre:</strong> {song.genre}</p>
                       <p><strong>Artist:</strong> {song.artist}</p>
                       <p><strong>Playlist:</strong> {song.playlist}</p>
                       {song.isFavorite && <p className="text-danger">‚ù§Ô∏è Favorite</p>}

                       <div className="mt-3">
                           <strong>Lyrics:</strong>
                           <pre className="mt-2" style={{ whiteSpace: 'pre-wrap' }}>
                               {song.lyrics}
                           </pre>
                       </div>

                       <button
                           className="btn btn-primary mt-3 me-2"
                           onClick={() => setIsEditing(true)}
                       >
                           Edit Song
                       </button>
                       <button
                           className="btn btn-secondary mt-3"
                           onClick={() => window.history.back()}
                       >
                           Back to Songbook
                       </button>
                   </div>
               </div>
           ) : isEditing ? (
               <div className="card">
                   <div className="card-body">
                       <h3>Edit Song</h3>
                       <SongForm 
                           formData={editForm}
                           setFormData={setEditForm}
                           handleSubmit={handleEditSubmit}
                           errors={errors}
                           buttonText="Save Changes"
                       />
                       <button
                           className="btn btn-secondary mt-3"
                           onClick={() => setIsEditing(false)}
                       >
                           Cancel
                       </button>
                   </div>
               </div>
           ) : null}
       </div>
   );
}
