import { useState } from 'react';
import { toast } from 'react-toastify';

export default function SongDetails({ songId }) {
    const [song, setSong] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [editForm, setEditForm] = useState(null);

    const fetchSongDetails = async () => {
      try {
        const response = await fetch(`http://localhost:3001/songs/${songId}`);
        if (!response.ok) throw new Error('Song not found');
        const data = await response.json();
        setSong(data);
        setEditForm(data);
      } catch (error) {
        toast.error('Failed to load song details');
        window.location.href = '/songbook';
      }
    };

    const handleUpdate = async (e) => {
      e.preventDefault();
      try {
        const response = await fetch(`http://localhost:3001/songs/${songId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(editForm),
        });

        if (!response.ok) throw new Error('Failed to update song');
        const updatedSong = await response.json();
        setSong(updatedSong);
        setIsEditing(false);
        toast.success('Song updated successfully!');
      } catch (error) {
        toast.error('Failed to update song');
      }
    };

    return (
      <div className="container mt-4">
        {!song ? (
          <button 
            className="btn btn-primary" 
            onClick={fetchSongDetails}
          >
            Load Song Details
          </button>
        ) : !isEditing ? (
          <div>
            <SongCard song={song} />
            <div className="mt-3">
              <button
                className="btn btn-primary me-2"
                onClick={() => setIsEditing(true)}
              >
                Edit
              </button>
              <a href="/songbook" className="btn btn-secondary">
                Back to Songbook
              </a>
            </div>
          </div>
        ) : (
            <form onSubmit={handleUpdate}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                value={editForm.title}
                onChange={e => setEditForm({...editForm, title: e.target.value})}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="lyrics" className="form-label">Lyrics</label>
              <textarea
                className="form-control"
                id="lyrics"
                rows="5"
                value={editForm.lyrics}
                onChange={e => setEditForm({...editForm, lyrics: e.target.value})}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="genre" className="form-label">Genre</label>
              <select
                className="form-select"
                id="genre"
                value={editForm.genre}
                onChange={e => setEditForm({...editForm, genre: e.target.value})}
              >
                <option value="pop">Pop</option>
                <option value="rock">Rock</option>
                <option value="hip-hop">Hip-Hop</option>
                <option value="r&b">R&B</option>
                {/* Add other genre options */}
              </select>
            </div>

            <button type="submit" className="btn btn-success me-2">
              Save Changes
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </form>
        )}
      </div>
    );
  }
