import React, { useState } from 'react';
import { toast } from 'react-toastify';
import SongCard from '../Components/SongCard';

export default function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      return;
    }

    setIsSearching(true);

    try {
      // Fetch all songs
      const response = await fetch('http://localhost:3001/songs');
      if (!response.ok) throw new Error('Failed to fetch songs');
      const allSongs = await response.json();

      // Filter songs based on search term (case-insensitive)
      const filteredSongs = allSongs.filter(song => 
        song.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        song.genre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (song.lyrics && song.lyrics.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (song.artist && song.artist.toLowerCase().includes(searchTerm.toLowerCase()))
      );

      setSearchResults(filteredSongs);
      setIsSearching(false);

      // Only show "no results" toast if search was actually performed
      if (filteredSongs.length === 0 && searchTerm.trim()) {
        toast.info('No matching songs found');
      }

    } catch (error) {
      console.error('Error:', error);
      setIsSearching(false);
      toast.error('Search failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <form onSubmit={handleSearch} className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for songs by title, artist, genre, or lyrics..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button 
            className="btn btn-primary" 
            type="submit"
            disabled={isSearching}
          >
            {isSearching ? 'Searching...' : 'Search'}
          </button>
        </div>
      </form>

      <div className="row">
        {isSearching ? (
          <div className="col">
            <p>Searching...</p>
          </div>
        ) : searchResults.length > 0 ? (
          <div className="col">
            <h3>Search Results</h3>
            {searchResults.map(song => (
              <div key={song.id} className="mb-3">
                <SongCard 
                  song={song}
                  showActions={false}
                />
              </div>
            ))}
          </div>
        ) : searchTerm && (
          <div className="col">
            <p>No results found</p>
          </div>
        )}
      </div>
    </div>
  );
}
