import React, { useState } from 'react';
import { toast } from 'react-toastify';

export default function SongDetails() {
  const [song, setSong] = useState(null);
  const songId = window.location.pathname.split('/').pop();

  const fetchSongDetails = async () => {
    try {
      const response = await fetch(`http://localhost:3001/songs/${songId}`);
      if (!response.ok) throw new Error('Song not found');
      const data = await response.json();
      setSong(data);
    } catch (error) {
      toast.error('Failed to load song details');
    }
  };

  // Call fetchSongDetails right away
  fetchSongDetails();

  return (
    <div>
      {song ? (
        <div className="card">
          <div className="card-body">
            <h3 className="card-title">{song.title}</h3>
            <h6 className="card-subtitle mb-2 text-muted">Genre: {song.genre}</h6>
            <p className="card-text">
              <strong>Artist ID:</strong> {song.artistId}<br />
              <strong>Playlist:</strong> {song.playlist}<br />
              {song.isFavorite && <span className="text-danger">❤️ Favorite</span>}
            </p>
            <div className="card-text mb-3">
              <strong>Lyrics:</strong><br />
              <pre className="mt-2" style={{ whiteSpace: 'pre-wrap' }}>
                {song.lyrics}
              </pre>
            </div>
            <button
              className="btn btn-secondary"
              onClick={() => window.location.href = '/songbook'}
            >
              Back to Songbook
            </button>
          </div>
        </div>
      ) : (
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}
    </div>
  );
}
