// Search.jsx
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-toastify';

export default function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState({
    songs: [],
    artists: [],
    albums: []
  });
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const [songsRes, artistsRes, albumsRes] = await Promise.all([
        fetch(`http://localhost:3001/songs?q=${searchTerm}`),
        fetch(`http://localhost:3001/artists?q=${searchTerm}`),
        fetch(`http://localhost:3001/albums?q=${searchTerm}`)
      ]);

      const [songs, artists, albums] = await Promise.all([
        songsRes.json(),
        artistsRes.json(),
        albumsRes.json()
      ]);

      setSearchResults({ songs, artists, albums });
      navigate(`/search/${searchTerm}`);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Search failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <form onSubmit={handleSearch} className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for songs, artists, or albums..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="btn btn-primary" type="submit">
            Search
          </button>
        </div>
      </form>

      <div className="row">
        {searchResults.songs?.length > 0 && (
          <div className="col-md-4">
            <h3>Songs</h3>
            <div className="list-group">
              {searchResults.songs.map(song => (
                <Link
                  key={song.id}
                  to={`/songbook/${song.id}`}
                  className="list-group-item list-group-item-action"
                >
                  {song.title}
                </Link>
              ))}
            </div>
          </div>
        )}

        {/* Similar sections for artists and albums */}
      </div>
    </div>
  );
}
