import React from 'react';
import {Link} from 'react-router-dom';
import Form from '../Pages/LyricsLog';


export default function Instruction() {
    return(

        <div className="container mx-auto px-16 py-24 ">
        <div className="border border-gray-700 rounded-2xl p-12 bg-[#1E1E1E]">
          <h1 className="text-2xl font-medium mb-6 text-gray-200">
            Welcome to melomemo!
          </h1>
          <p className="text-2xl leading-normal text-gray-200">
            You can collect your favorite lyrics and songs and document them in a deeper way.
            <p>To add songs, go to <Link to="/lyrics-log">Lyrics Log</Link></p>
            <p>To view your log history, go to <Link to="/songbook">Your Songbook</Link></p>
            <p>To search for music on Spotify, go to <Link to="/search">Search</Link></p>
          </p>
        </div>
      </div>
    )
}
