import React, { useState } from 'react';
import { toast } from 'react-toastify';
import SongCard from '../components/SongCard';

export default function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState({
    songs: [],
    artists: []
  });

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      toast.error('Please enter a search term');
      return;
    }

    try {
      // Get all songs first
      const response = await fetch('http://localhost:3001/songs');
      const allSongs = await response.json();

      // Filter songs based on search term
      const filteredSongs = allSongs.filter(song => 
        song.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        song.genre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        song.lyrics.toLowerCase().includes(searchTerm.toLowerCase())
      );

      // Also search for artists
      const artistResponse = await fetch('http://localhost:3001/artists');
      const allArtists = await artistResponse.json();
      const filteredArtists = allArtists.filter(artist =>
        artist.name.toLowerCase().includes(searchTerm.toLowerCase())
      );

      setSearchResults({
        songs: filteredSongs,
        artists: filteredArtists
      });

      if (filteredSongs.length === 0 && filteredArtists.length === 0) {
        toast.info('No results found');
      }

    } catch (error) {
      console.error('Error:', error);
      toast.error('Search failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <form onSubmit={handleSearch} className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for songs by title, genre, or lyrics..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="btn btn-primary" type="submit">
            Search
          </button>
        </div>
      </form>

      <div className="row">
        {searchResults.songs.length > 0 && (
          <div className="col-md-8">
            <h3>Songs</h3>
            {searchResults.songs.map(song => (
              <SongCard 
                key={song.id} 
                song={song}
                showActions={false}
              />
            ))}
          </div>
        )}

        {searchResults.artists.length > 0 && (
          <div className="col-md-4">
            <h3>Artists</h3>
            <div className="list-group">
              {searchResults.artists.map(artist => (
                <div key={artist.id} className="list-group-item">
                  <h5>{artist.name}</h5>
                  <p className="mb-0">{artist.bio}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
