// Songbook.jsx
import React, { useState } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { toast } from 'react-toastify';
import SongCard from '../Components/SongCard';

export default function Songbook() {
  const [songs, setSongs] = useState([]);

  // GET - Fetch songs when component loads or refreshes
  const fetchSongs = async () => {
    try {
      const response = await fetch('http://localhost:3001/songs');
      if (!response.ok) throw new Error('Failed to fetch songs');
      const data = await response.json();
      setSongs(data);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to load songs');
    }
  };

  // DELETE - Remove a song
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/songs/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete song');
      setSongs(songs.filter(song => song.id !== id));
      toast.success('Song deleted successfully!');
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to delete song');
    }
  };

  // Load songs when button is clicked
  const handleLoadSongs = () => {
    fetchSongs();
  };

  return (
    <div className="container mt-4">
        <div className="row">
            <div className="col-md-4">
                <h2>Your Songs</h2>
                    <button 
                        className="btn btn-primary mb-3"
                        onClick={handleLoadSongs}
                    >
                        Load Songs
                    </button>
                    <div className="list-group">
                        {songs.map(song => (
                            <div key={song.id}>
                            <SongCard 
                            song={song}
                            showActions={true}
                            onDelete={handleDelete}
                            />
                    </div>
                ))}
            </div>
        </div>
            <div className="col-md-8 border-start">
                <Outlet />
            </div>
        </div>
    </div>
  );
}
